import React from "react";
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import { Box, Typography } from "@mui/material";
import { DateCalendar } from '@mui/x-date-pickers/DateCalendar';
import { DemoItem } from '@mui/x-date-pickers/internals/demo';
import dayjs from 'dayjs';

// Importações do FullCalendar
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';

const Home = () => {
    return (
        <LocalizationProvider dateAdapter={AdapterDayjs}>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', height: '100vh', padding: 2 }}>
                {/* FullCalendar à esquerda ocupando metade da tela */}
                <Box sx={{ width: '50%', backgroundColor: '#f0f0f0', padding: 2 }}>
                    <Typography variant="h6" gutterBottom>
                        Calendário Grande
                    </Typography>
                    {/* Componente FullCalendar */}
                    <FullCalendar
                        plugins={[dayGridPlugin]}
                        initialView="dayGridMonth"
                        events={[
                            { title: 'Processo 1', date: '2024-08-28' },
                            { title: 'Processo 2', date: '2024-08-29' }
                        ]}
                        locale="pt-br" // Para traduzir o calendário para o português
                    />
                </Box>

                {/* Calendários pequenos na direita */}
                <Box sx={{ width: '35%', backgroundColor: '#ffffff', padding: 2 }}>
                    <Typography variant="h6" gutterBottom>
                        Calendários
                    </Typography>
                    <DemoItem label={'"day"'}>
                        <DateCalendar views={['day']} />
                    </DemoItem>
                </Box>
            </Box>
        </LocalizationProvider>
    );
};

export default Home;
