import React, { useState, useEffect, useRef } from "react";
import { TextField, Button, ButtonGroup, Snackbar, Alert, Chip, Box, Grid, MenuItem } from "@mui/material";
import { useNavigate } from "react-router-dom";
import Autocomplete from "@mui/lab/Autocomplete";

// Enum values and descriptions
const statusOptions = [
  { value: 'ATIVO', label: 'Ativo' },
  { value: 'INATIVO', label: 'Inativo' },
  { value: 'PENDENTE', label: 'Pendente' },
  { value: 'CONCLUIDO', label: 'Concluído' }
];

const FormField = ({ label, value, onChange, error, helperText, multiline, rows, type, options }) => (
  <Box sx={{ marginBottom: 2 }}>
    <div style={{ color: '#838383', fontSize: 12, fontFamily: 'Inter', fontWeight: '400', wordWrap: 'break-word', width: 200 }}>
      {label}
    </div>
    <TextField
      hiddenLabel
      variant="filled"
      size="small"
      multiline={multiline}
      rows={rows}
      type={type}
      select={!!options}
      sx={{
        width: '250px',
        '& .MuiInputBase-root': {
          padding: '0 14px',
          '& input, & textarea': {
            padding: '30',
            fontSize: '13px',
          },
        },
        '& .MuiFilledInput-root': {
          padding: '0',
        },
      }}
      value={value}
      onChange={onChange}
      error={error}
      helperText={helperText}
    >
      {options && options.map(option => (
        <MenuItem key={option.value} value={option.value}>
          {option.label}
        </MenuItem>
      ))}
    </TextField>
  </Box>
);

const Projetos = () => {
  const navigate = useNavigate();
  const [state, setState] = useState({
    titulo: '',
    descricao: '',
    prazo: '',
    registroReceita: '',
    clienteId: null, // Set initial value to null for proper comparison
    status: '',
    descricaoStatus: ''
  });
  const [errors, setErrors] = useState({});
  const [snackbarOpen, setSnackbarOpen] = useState(false);
  const [snackbarMessage, setSnackbarMessage] = useState('');
  const [snackbarSeverity, setSnackbarSeverity] = useState('success');
  const [tags, setTags] = useState([]);
  const [clientes, setClientes] = useState([]);
  const tagInputRef = useRef(null);

  useEffect(() => {
    // Carregar clientes ao montar o componente
    const fetchClientes = async () => {
      try {
        const response = await fetch("http://localhost:8080/api/cliente");
        const data = await response.json();
        setClientes(data);
      } catch (error) {
        console.error("Erro ao buscar clientes:", error);
      }
    };

    fetchClientes();
  }, []);

  const handleChange = (campo) => (event, newValue) => {
    if (campo === 'clienteId') {
      setState({ ...state, clienteId: newValue ? newValue : null });
    } else {
      setState({ ...state, [campo]: event.target.value });
    }
  };

  const handleTagChange = (event, newValue) => {
    setTags(newValue);
    if (tagInputRef.current) {
      setTimeout(() => {
        tagInputRef.current.focus();
      }, 100);
    }
  };

  const handleDeleteTag = (tagToDelete) => () => {
    setTags((tags) => tags.filter((tag) => tag !== tagToDelete));
  };

  const validateFields = () => {
    const newErrors = {};
    if (!state.titulo) newErrors.titulo = 'Titulo é obrigatório';
    if (!state.descricao) newErrors.descricao = 'Descrição é obrigatória';
    if (!state.prazo) newErrors.prazo = 'Prazo é obrigatório';
    if (!state.registroReceita) newErrors.registroReceita = 'Registro é obrigatório';
    if (!state.clienteId) newErrors.clienteId = 'Cliente é obrigatório';
    if (!state.status) newErrors.status = 'Status é obrigatório';
    if (!state.descricaoStatus) newErrors.descricaoStatus = 'Descrição do Status é obrigatória';
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const salvar = async () => {
    if (!validateFields()) {
      setSnackbarMessage("Preencha todos os campos obrigatórios.");
      setSnackbarSeverity("error");
      setSnackbarOpen(true);
      return;
    }

    const processo = {
      titulo: state.titulo,
      descricao: state.descricao,
      prazo: state.prazo,
      registroReceita: state.registroReceita,
      tags: tags,
      status: state.status,
      historico: [],
      cliente: state.clienteId ? state.clienteId.id : null, // Enviando apenas o ID do cliente
      files: []
    };

    try {
      const response = await fetch('http://localhost:8080/api/processo', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(processo)
      });

      if (response.ok) {
        const responseData = await response.json();
        const numeroProcesso = responseData.numeroProcesso;

        setSnackbarMessage("Processo salvo com sucesso!");
        setSnackbarSeverity("success");
        setSnackbarOpen(true);

        navigate("/sessao/status", { state: { numeroProcesso } });
      } else {
        setSnackbarMessage("Erro ao salvar o processo.");
        setSnackbarSeverity("error");
        setSnackbarOpen(true);
      }
    } catch (error) {
      setSnackbarMessage("Erro ao salvar o processo.");
      setSnackbarSeverity("error");
      setSnackbarOpen(true);
    }
  };

  return (
    <Box>
      <Grid container spacing={2}>
        <Grid item xs={12}>
          <h1 style={{ fontFamily: 'Inter', fontSize: 34 }}>Novo Projeto</h1>
        </Grid>
        <Grid item xs={12}>
          <FormField
            label="Título"
            value={state.titulo}
            onChange={handleChange('titulo')}
            error={!!errors.titulo}
            helperText={errors.titulo}
          />
          <FormField
            label="Descrição"
            value={state.descricao}
            onChange={handleChange('descricao')}
            error={!!errors.descricao}
            helperText={errors.descricao}
            multiline
            rows={4}
          />
          <FormField
            label="Prazo"
            value={state.prazo}
            onChange={handleChange('prazo')}
            error={!!errors.prazo}
            helperText={errors.prazo}
            type="date"
          />
          <FormField
            label="Registro Receita"
            value={state.registroReceita}
            onChange={handleChange('registroReceita')}
            error={!!errors.registroReceita}
            helperText={errors.registroReceita}
          />
          <Autocomplete
            options={clientes}
            getOptionLabel={(option) => option.nome} // Assuming that Cliente has the 'nome' field
            isOptionEqualToValue={(option, value) => option.id === value.id} // Proper comparison logic
            onChange={handleChange('clienteId')}
            value={state.clienteId}
            renderInput={(params) => (
              <TextField
                {...params}
                variant="filled"
                hiddenLabel
                size="small"
                sx={{
                  width: '250px',
                  '& .MuiInputBase-root': {
                    height: '32px',
                    padding: '0 14px',
                    '& input': {
                      height: '20px',
                      padding: '0',
                      fontSize: '13px',
                    },
                  },
                }}
              />
            )}
          />
          <FormField
            label="Status"
            value={state.status}
            onChange={handleChange('status')}
            error={!!errors.status}
            helperText={errors.status}
            options={statusOptions}
          />
        </Grid>
        <Grid item xs={12}>
          <TextField
            inputRef={tagInputRef}
            hiddenLabel
            variant="filled"
            size="small"
            sx={{
              width: '250px',
              '& .MuiInputBase-root': {
                height: '32px',
                padding: '0 14px',
                '& input': {
                  height: '20px',
                  padding: '0',
                  fontSize: '13px',
                },
              },
            }}
            placeholder="Adicionar Tags"
            onKeyDown={(event) => {
              if (event.key === 'Enter' && event.target.value) {
                setTags([...tags, event.target.value]);
                event.target.value = '';
              }
            }}
          />
          <Box sx={{ display: 'flex', flexWrap: 'wrap', marginTop: 1 }}>
            {tags.map((tag, index) => (
              <Chip
                key={index}
                label={tag}
                onDelete={handleDeleteTag(tag)}
                sx={{ marginRight: 1, marginBottom: 1 }}
              />
            ))}
          </Box>
        </Grid>
        <Grid item xs={12}>
          <ButtonGroup variant="contained" aria-label="outlined primary button group">
            <Button onClick={salvar}>Salvar</Button>
            <Button onClick={() => navigate('/sessao/lista')}>Cancelar</Button>
          </ButtonGroup>
        </Grid>
      </Grid>
      <Snackbar open={snackbarOpen} autoHideDuration={6000} onClose={() => setSnackbarOpen(false)}>
        <Alert onClose={() => setSnackbarOpen(false)} severity={snackbarSeverity}>
          {snackbarMessage}
        </Alert>
      </Snackbar>
    </Box>
  );
};

export default Projetos;
