import React from "react";
import './login.css';
import TextField from '@mui/material/TextField';
import axios from "axios";
import Button from '@mui/material/Button';
import ButtonGroup from '@mui/material/ButtonGroup';

export default class Login extends React.Component{

    constructor(props) {
        super(props);
        this.state = {
          login: '',
          senha:''
        };
        this.handleChange = this.handleChange.bind(this);
      }
      handleChange = (campo) => (event) => {
        this.setState({ [campo]: event.target.value });
      }

      logar = async() => {
        axios.post('http://localhost:8080/api/advogado',{
          login: this.state.login,
          senha: this.state.senha,
         }).then(response => 
         {
           console.log(response);
         }
         ).catch(error => 
           {
             console.log(error.response);
           });
           console.log('request finished');
       }

    render(){
        return(
            <div className="container">
                <div className="inner-container">
                    <div className="box">
                        <div className="box-background"></div>
                        <div className="logo">LOGO</div>
                        <div className="form-group">
                            <div className="label login-label">Login</div>
                            <TextField
                                hiddenLabel
                                id="filled-hidden-label-small"
                                defaultValue=""
                                variant="filled"
                                size="small"
                                style={{ marginTop: '15px', width: '245px' }}
                                value={this.state.login}
                                onChange={this.handleChange('login')}
                            />
                            <div className="label password-label">Senha</div>
                            <TextField
                                hiddenLabel
                                id="filled-hidden-label-small"
                                defaultValue=""
                                variant="filled"
                                size="small"
                                style={{ marginTop: '25px', width: '245px' }}
                                value={this.state.senha}
                                onChange={this.handleChange('senha')}
                            />
                        </div>
                        <div className="button-container">
                            <Button onClick={this.logar} variant="contained" style={{ width: '240px', height: '27px', backgroundColor: 'grey', marginLeft: '-10px' }}>
                                ENTRAR
                            </Button>
                        </div>
                        <div className="forgot-password">Esqueceu a senha?</div>
                        <div className="welcome">Bem Vindo ao SGP</div>
                    </div>
                </div>
            </div>

          )
    }

    styles = {
        colMd6:{
            position: 'relative',
            left: '300x'
        }
    };


}
