import React, { useState, useRef } from "react";
import "./Projetos.css";
import { Button, ButtonGroup, Snackbar, Alert, Chip, Box, MenuItem } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import Autocomplete from '@mui/lab/Autocomplete';
import CustomTextField from '../../../components/CustomTextField';
import MuiTextField from '@mui/material/TextField';


const Projetos = () => {
    const navigate = useNavigate();
  
    const [state, setState] = useState({
        titulo: '',
        descricao: '',
        prazo: '',
        registroReceita: '',
        tags: [],
        clienteId: '',
        status: '',
        descricaoStatus: ''
    });

    const [snackbarOpen, setSnackbarOpen] = useState(false);
    const [snackbarMessage, setSnackbarMessage] = useState('');
    const [snackbarSeverity, setSnackbarSeverity] = useState('success');
    const [tags, setTags] = useState([]);
    const tagInputRef = useRef(null);

    const handleChange = (campo) => (event) => {
        setState({ ...state, [campo]: event.target.value });
    }

    const handleTagChange = (event, newValue) => {
        setTags(newValue);
        if (tagInputRef.current) {
            setTimeout(() => {
                tagInputRef.current.focus();
            }, 100);
        }
    };

    const handleDeleteTag = (tagToDelete) => () => {
        setTags((tags) => tags.filter((tag) => tag !== tagToDelete));
    };

    const salvar = async () => {
        const processo = {
            titulo: state.titulo,
            descricao: state.descricao,
            prazo: state.prazo,
            registroReceita: state.registroReceita,
            tags: tags,
            cliente: state.clienteId,
            status: state.status,
            descricaoStatus: state.descricaoStatus
        };
    
        try {
            const response = await fetch('/api/processos', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(processo)
            });
    
            if (response.ok) {
                setSnackbarMessage("Processo salvo com sucesso!");
                setSnackbarSeverity("success");
                setSnackbarOpen(true);
                // Limpar os campos se necessÃ¡rio
            } else {
                setSnackbarMessage("Erro ao salvar o processo.");
                setSnackbarSeverity("error");
                setSnackbarOpen(true);
            }
        } catch (error) {
            setSnackbarMessage("Erro ao salvar o processo.");
            setSnackbarSeverity("error");
            setSnackbarOpen(true);
        }
    };
   
    const cancel = () => {
        console.log('cancel');
    }

    const goToStatus = () => {
        navigate("/sessao/status");
    }

    const goToAnexos = () => {
        navigate("/sessao/anexos");
    }

    const handleCloseSnackbar = () => {
        setSnackbarOpen(false);
    };

    return (
        <div className="container">
            <div className="row">
                <div className="col-lg-12">
                    <div className="bs-component">
                        <div style={{marginTop: '80px'}}>
                            <ButtonGroup style={{height:'30px'}} variant="contained" aria-label="Basic button group">
                                <Button style={{ backgroundColor: 'grey', fontSize: 10, width: 120 }}>Processos</Button>
                                <Button style={{ backgroundColor: 'white', color: 'grey' , fontSize: 10, width: 120 }} onClick={goToStatus}>Status</Button>
                                <Button style={{ backgroundColor: 'white', color: 'grey' , fontSize: 10, alignItems: 'center', width: 120  }} onClick={goToAnexos}>Anexos</Button>
                            </ButtonGroup>
                            <div style={{width: 1100, height: 470, background: 'white', border: '2px #838383 solid'}}>
                                <div style={{marginTop: '25px', marginLeft: '30px'}}>
                                    <CustomTextField
                                        label="TÃTULO"
                                        value={state.titulo}
                                        onChange={handleChange('titulo')}
                                        style={{
                                            '& .MuiInputBase-root': {
                                                height: '32px',
                                                padding: '0 14px',
                                                '& input': {
                                                    height: '20px',
                                                    padding: '0',
                                                    fontSize: '13px',
                                                },
                                            },
                                        }}
                                    />

                                    <CustomTextField
                                        label="DESCRIÃÃO"
                                        value={state.descricao}
                                        onChange={handleChange('descricao')}
                                        multiline
                                        rows={4}
                                        style={{
                                            '& .MuiInputBase-root': {
                                                height: 'auto',
                                                padding: '0 14px',
                                                '& textarea': {
                                                    height: '20px',
                                                    fontSize: '13px',
                                                    textAlign: "left",
                                                },
                                            },
                                        }}
                                    />

                                    <div style={{marginTop: '20px', marginLeft: '50px'}}>
                                        <CustomTextField
                                            label="PRAZO"
                                            type="date"
                                            value={state.prazo}
                                            onChange={handleChange('prazo')}
                                            style={{
                                                width: '130px',
                                                '& .MuiInputBase-root': {
                                                    height: '32px',
                                                    padding: '0 14px',
                                                    '& input': {
                                                        height: '20px',
                                                        padding: '0',
                                                        fontSize: '13px',
                                                    },
                                                },
                                            }}
                                        />
                                    </div>

                                    <CustomTextField
                                        label="REGISTRO DA RECEITA FEDERAL"
                                        value={state.registroReceita}
                                        onChange={handleChange('registroReceita')}
                                        style={{
                                            '& .MuiInputBase-root': {
                                                height: '32px',
                                                padding: '0 14px',
                                                '& input': {
                                                    height: '20px',
                                                    padding: '0',
                                                    fontSize: '13px',
                                                },
                                            },
                                        }}
                                    />

                                    <div style={{marginTop: '-365px', marginLeft: '700px'}}>
                                        <div style={{ width: 200, height: 40, color: '#838383', fontSize: 12, fontFamily: 'Inter', fontWeight: '400', wordWrap: 'break-word'}}>TAGS</div>
                                        <Autocomplete
                                            multiple
                                            freeSolo
                                            options={[]}
                                            value={tags}
                                            onChange={handleTagChange}
                                            renderTags={() => null} // Desabilita a renderizaÃ§Ã£o das tags dentro do campo de entrada
                                            renderInput={(params) => (
                                                <MuiTextField
                                                    {...params}
                                                    inputRef={tagInputRef}
                                                    variant="filled"
                                                    hiddenLabel
                                                    size="small"
                                                    sx={{
                                                        marginTop: '-15px',
                                                        width: '250px',
                                                        '& .MuiInputBase-root': {
                                                            height: '32px',
                                                            padding: '0 14px',
                                                            '& input': {
                                                                height: '20px',
                                                                padding: '0',
                                                                fontSize: '13px',
                                                            },
                                                        },
                                                    }}
                                                />
                                            )}
                                        />
                                        <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 0.5, marginTop: '8px' }}>
                                            {tags.map((tag, index) => (
                                                <Chip key={index} label={tag} onDelete={handleDeleteTag(tag)} />
                                            ))}
                                        </Box>

                                        <CustomTextField
                                            label="CLIENTE"
                                            value={state.clienteId}
                                            onChange={handleChange('clienteId')}
                                            style={{
                                                '& .MuiInputBase-root': {
                                                    height: '32px',
                                                    padding: '0 14px',
                                                    '& input': {
                                                        height: '20px',
                                                        padding: '0',
                                                        fontSize: '13px',
                                                    },
                                                },
                                            }}
                                        />

                                        <CustomTextField
                                            label="STATUS"
                                            value={state.status}
                                            onChange={handleChange('status')}
                                            component={
                                                <MuiTextField
                                                    select
                                                    hiddenLabel
                                                    variant="filled"
                                                    size="small"
                                                    sx={{
                                                        marginTop: '-15px',
                                                        width: '250px',
                                                        '& .MuiInputBase-root': {
                                                            height: '32px',
                                                            padding: '0 14px',
                                                            '& input': {
                                                                height: '20px',
                                                                padding: '0',
                                                                fontSize: '13px',
                                                            },
                                                        },
                                                    }}
                                                >
                                                    <MenuItem value="Pendente">Pendente</MenuItem>
                                                    <MenuItem value="Ativo">Ativo</MenuItem>
                                                    <MenuItem value="ConcluÃ­do">ConcluÃ­do</MenuItem>
                                                    <MenuItem value="Inativo">Inativo</MenuItem>
                                                </MuiTextField>
                                            }
                                        />

                                        <CustomTextField
                                            label="DESCRIÃÃO STATUS"
                                            value={state.descricaoStatus}
