import React from "react";
import { Route, BrowserRouter, Switch, Redirect } from "react-router-dom";
import NavBar from "../components/NavBar"
import login from "../screens/login/login";
import CreateAdvogado from "../screens/createAdvogado/CreateAdvogado";
import UpdateAdvogado from "../screens/updateAdvogado/UpdateAdvogado";
import DeleteAdvogado from "../screens/deleteAdvogado/DeleteAdvogado";
import ListarUsuario from "../screens/listarUsuarios/ListarUsuarios"
import { AuthConsumer } from './SessionProvider';


function RestrictedRoute({ component: Component, show, ...props }) {
    return (
        <Route exact {...props} render={(componentProps) => {
            if (show) {
                return (
                    <Component {...componentProps} />
                )
            } else {
                return (
                    <Redirect to={{ pathname: '/login', state: { from: componentProps.location } }} />
                )
            }
        }} />
    )
}

function AppRoutes(){
    return(


        <BrowserRouter>
            <Route component={login} path="/" exact />
                <Route path="/sessao/">
                <NavBar />
                <Route component={CreateAdvogado} path="/sessao/createAdvogado" />
                <RestrictedRoute component={ListarUsuario} path="/sessao/listarUsuarios" />
                <Route component={UpdateAdvogado} path="/sessao/updateAdvogado" />
                <Route component={DeleteAdvogado} path="/sessao/deleteAdvogado" />
            </Route>
        </BrowserRouter>
    );
}
export default () => (
    <AuthConsumer>
        { (context) => (<AppRoutes isAuthenticated={context.isAuthenticated} />) }
    </AuthConsumer>
)
