import React from "react";
import "./ListarUsuarios.css";
import axios from "axios";
import Box from '@mui/material/Box';
import { DataGrid } from '@mui/x-data-grid';

export default class ListarUsuario extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      rows: [],
      loading: true
    };
  }

  async componentDidMount() {
    try {
      const [advogadosResponse, clientesResponse, associadosResponse] = await Promise.all([
        axios.get('http://localhost:8080/api/advogado'),
        axios.get('http://localhost:8080/api/cliente'),
        axios.get('http://localhost:8080/api/associado')
      ]);

      const advogados = advogadosResponse.data.map(item => ({ ...item, tipo: 'Advogado' }));
      const clientes = clientesResponse.data.map(item => ({ ...item, tipo: 'Cliente' }));
      const associados = associadosResponse.data.map(item => ({ ...item, tipo: 'Associado' }));

      const combinedRows = [...advogados, ...clientes, ...associados];

      this.setState({ rows: combinedRows, loading: false });
    } catch (error) {
      console.error('Erro ao buscar os dados:', error);
      this.setState({ loading: false });
    }
  }

  render() {
    const { rows, loading } = this.state;

    const columns = [
      { field: 'id', headerName: 'ID', width: 90 },
      {
        field: 'nome',
        headerName: 'Nome',
        width: 150,
        editable: true,
      },
      {
        field: 'login',
        headerName: 'Email',
        width: 150,
        editable: true,
      },
      {
        field: 'tipo',
        headerName: 'Tipo',
        width: 150,
      },
    ];

    return (
      <div className="container">
        <div className="row">
          <div className="col-lg-12">
            <div className="bs-component">
              <div style={{ marginTop: '70px' }}>
                {loading ? (
                  <p>Carregando...</p>
                ) : (
                  <Box sx={{ height: 400, width: '100%' }}>
                    <DataGrid
                      rows={rows}
                      columns={columns}
                      pageSize={5}
                      rowsPerPageOptions={[5, 10, 20]}
                      disableSelectionOnClick
                    />
                  </Box>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
