import React from "react";
import { Route, BrowserRouter, Redirect } from "react-router-dom";
import NavBar from "../components/NavBar"
import login from "../screens/login/login";
import CreateAdvogado from "../screens/createAdvogado/CreateAdvogado";
import UpdateAdvogado from "../screens/updateAdvogado/UpdateAdvogado";
import DeleteAdvogado from "../screens/deleteAdvogado/DeleteAdvogado";
import ListarUsuario from "../screens/listarUsuarios/ListarUsuarios"
import AuthProvider from './AuthContext';

function RestrictedRoute({ component: Component, ...props }) {
    const { isAuthenticated } = useContext(AuthContext);

    return (
        <Route
            {...props}
            render={(componentProps) => {
                if (isAuthenticated) {
                    return <Component {...componentProps} />;
                } else {
                    return (
                        <Redirect
                            to={{ pathname: '/login', state: { from: componentProps.location } }}
                        />
                    );
                }
            }}
        />
    );
}


function AppRoutes() {
    return (
        <AuthProvider>
            <BrowserRouter>
                <Switch>
                    <Route exact path="/" component={login} />
                    <Route path="/sessao/">
                        <NavBar />
                        <Route path="/sessao/createAdvogado" component={CreateAdvogado} />
                        <RestrictedRoute path="/sessao/listarUsuarios" component={ListarUsuario} />
                        <Route path="/sessao/updateAdvogado" component={UpdateAdvogado} />
                        <Route path="/sessao/deleteAdvogado" component={DeleteAdvogado} />
                    </Route>
                </Switch>
            </BrowserRouter>
        </AuthProvider>
    );
}
export default AppRoutes;
