import React, { useState, useEffect } from "react";
import "./Casos.css";
import TextField from '@mui/material/TextField';
import axios from "axios";
import Snackbar from '@mui/material/Snackbar';
import Alert from '@mui/material/Alert';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import Typography from '@mui/material/Typography';

const Casos = () => {
    const [processos, setProcessos] = useState([]);
    const [snackbarOpen, setSnackbarOpen] = useState(false);
    const [snackbarMessage, setSnackbarMessage] = useState('');
    const [snackbarSeverity, setSnackbarSeverity] = useState('success');
    const [searchQuery, setSearchQuery] = useState('');
    const [filterQuery, setFilterQuery] = useState('');

    useEffect(() => {
        // Função para buscar processos do backend
        const fetchProcessos = async () => {
            try {
                const response = await axios.get('http://localhost:8080/api/processo');
                setProcessos(response.data);
            } catch (error) {
                setSnackbarMessage('Erro ao buscar processos!');
                setSnackbarSeverity('error');
                setSnackbarOpen(true);
            }
        };
        fetchProcessos();
    }, []);

    const handleSearchChange = (event) => {
        setSearchQuery(event.target.value);
    };

    const handleFilterChange = (event) => {
        setFilterQuery(event.target.value);
    };

    const handleCloseSnackbar = () => {
        setSnackbarOpen(false);
    };

    // Filtrando os processos com base na pesquisa e filtro
    const filteredProcessos = processos.filter(processo => 
        (processo.titulo && processo.titulo.toLowerCase().includes(searchQuery.toLowerCase())) &&
        (filterQuery === '' || processo.status === filterQuery)
    );

    return (
        <div className="container mt-5">
            <div className="row">
                {/* Lado Esquerdo - Pesquisa e Filtro */}
                <div style={{ marginTop: '10px' }}>
                <div className="col-md-4 mb-4">
                    <TextField 
                        label="Pesquisar Processos" 
                        variant="outlined" 
                        fullWidth 
                        onChange={handleSearchChange} 
                        className="mb-3"
                    />
                    <TextField 
                        label="Filtrar por Status" 
                        variant="outlined" 
                        fullWidth 
                        onChange={handleFilterChange} 
                    />
                </div>

                {/* Lado Direito - Card de Processos */}
                <div className="col-md-8">
                    {filteredProcessos.length > 0 ? (
                        filteredProcessos.map(processo => (
                            <Card className="mb-4" key={processo.id}>
                                <CardContent>
                                    <Typography variant="h5" component="div">
                                        {processo.titulo}
                                    </Typography>
                                    <Typography variant="body2" color="text.secondary" gutterBottom>
                                        {processo.descricao}
                                    </Typography>
                                    <Typography variant="body2" color="text.secondary">
                                        Prazo: {new Date(processo.prazo).toLocaleDateString()}
                                    </Typography>
                                    <Typography variant="body2" color="text.secondary">
                                        Status: {processo.status}
                                    </Typography>
                                </CardContent>
                            </Card>
                        ))
                    ) : (
                        <Typography variant="body1">Nenhum processo encontrado.</Typography>
                    )}
                </div>
            </div>
            <Snackbar 
                open={snackbarOpen} 
                autoHideDuration={6000} 
                onClose={handleCloseSnackbar}
            >
                <Alert 
                    onClose={handleCloseSnackbar} 
                    severity={snackbarSeverity} 
                    sx={{ width: '100%' }}
                >
                    {snackbarMessage}
                </Alert>
            </Snackbar>
        </div>
        </div>
    );
}

export default Casos;
