import React, { useState, useRef } from "react";
import "./Status.css";
import { TextField, Button, ButtonGroup, Snackbar, Alert, Chip, Box, Grid, MenuItem } from "@mui/material";
import { useNavigate } from 'react-router-dom';
import CustomTextField from '../../../components/CustomTextField';

const statusOptions = [
    { value: 'ATIVO', label: 'Ativo' },
    { value: 'INATIVO', label: 'Inativo' },
    { value: 'PENDENTE', label: 'Pendente' },
    { value: 'CONCLUIDO', label: 'Concluído' }
];

const FormField = ({ label, value, onChange, error, helperText, multiline, rows, type, options }) => (
    <Box sx={{ marginBottom: 2 }}>
      <div style={{ color: '#838383', fontSize: 12, fontFamily: 'Inter', fontWeight: '400', wordWrap: 'break-word', width: 200 }}>
        {label}
      </div>
      <TextField
        hiddenLabel
        variant="filled"
        size="small"
        multiline={multiline}
        rows={rows}
        type={type}
        select={!!options}
        sx={{
          width: '250px',
          '& .MuiInputBase-root': {
            padding: '0 14px',
            '& input, & textarea': {
              padding: '30',
              fontSize: '13px',
            },
          },
          '& .MuiFilledInput-root': {
            padding: '0',
          },
        }}
        value={value}
        onChange={onChange}
        error={error}
        helperText={helperText}
      >
        {options && options.map(option => (
          <MenuItem key={option.value} value={option.value}>
            {option.label}
          </MenuItem>
        ))}
      </TextField>
    </Box>
  );

const Status = () => {
    const navigate = useNavigate();
    const [state, setState] = useState({
        status: '',
        descricaoStatus: ''
    });

    const [snackbarOpen, setSnackbarOpen] = useState(false);
    const [snackbarMessage, setSnackbarMessage] = useState('');
    const [snackbarSeverity, setSnackbarSeverity] = useState('success');

    const handleChange = (campo) => (event) => {
        setState({ ...state, [campo]: event.target.value });
    }

    const salvarStts = async () => {
        const processo = {
            titulo: state.titulo,
            descricao: state.descricao,
            prazo: state.prazo,
            registroReceita: state.registroReceita,
            status: state.status,
            descricaoStatus: state.descricaoStatus
        };

        try {
            const response = await fetch('/api/processos', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(processo)
            });

            if (response.ok) {
                const responseData = await response.json();
                const numeroProcesso = responseData.numeroProcesso;

                setSnackbarMessage("Processo salvo com sucesso!");
                setSnackbarSeverity("success");
                setSnackbarOpen(true);

                navigate("/sessao/status", { state: { numeroProcesso } });
            } else {
                setSnackbarMessage("Erro ao salvar o processo.");
                setSnackbarSeverity("error");
                setSnackbarOpen(true);
            }
        } catch (error) {
            setSnackbarMessage("Erro ao salvar o processo.");
            setSnackbarSeverity("error");
            setSnackbarOpen(true);
        }
    }

    const cancel = () => {
        console.log('cancel');
    }

    const handleCloseSnackbar = () => {
        setSnackbarOpen(false);
    };

    return (
        <div className="container">
            <div className="row">
                <div className="col-lg-12">
                    <div className="bs-component">
                        <div style={{ marginTop: '80px' }}>
                            <ButtonGroup style={{ height: '30px' }} variant="contained" aria-label="Basic button group">
                                <Button style={{ backgroundColor: 'white', color: 'grey', fontSize: 10, width: 120 }} onClick={() => navigate("/sessao/projetos")}>Processos</Button>
                                <Button style={{ backgroundColor: 'grey', fontSize: 10, width: 120 }}>Status</Button>
                                <Button style={{ backgroundColor: 'white', color: 'grey', fontSize: 10, width: 120 }} onClick={() => navigate("/sessao/anexos")}>Anexos</Button>
                            </ButtonGroup>
                            <div style={{ width: 1100, height: 470, background: 'white', border: '2px #838383 solid' }}>
                                <div style={{ marginTop: '120px', marginLeft: '30px' }}>
                                    <div style={{ marginTop: '-65px', marginLeft: '450px' }}>
                                    <FormField
                      label="STATUS"
                      value={state.status}
                      onChange={handleChange('status')}
                      options={statusOptions}
                    />
                                        <div style={{ width: 80, height: 40, color: '#838383', fontSize: 12, fontFamily: 'Inter', fontWeight: '400', wordWrap: 'break-word', marginTop: '15px' }}>DESCRIÇÃO DO STATUS</div>
                                        <TextField
                                            hiddenLabel
                                            variant="filled"
                                            size="small"
                                            multiline
                                            rows={4}
                                            sx={{
                                                marginTop: '-5px',
                                                width: '250px',
                                                '& .MuiInputBase-root': {
                                                    height: 'auto',
                                                    padding: '0 14px',
                                                    '& textarea': {
                                                        height: '20px',
                                                        fontSize: '13px',
                                                        textAlign: "left",
                                                    },
                                                },
                                            }}
                                            value={state.descricaoStatus}
                                            onChange={handleChange('descricaoStatus')}
                                        />
                                    </div>
                                </div>
                            </div>
                            <div style={{ marginTop: '20px' }}>
                                <Button onClick={cancel} variant="contained" style={{ width: 120, height: 40, backgroundColor: 'grey' }}>Cancelar</Button>
                                <Button onClick={salvarStts} variant="contained" style={{ width: 120, height: 40, backgroundColor: 'grey', marginLeft: '850px' }}>Salvar</Button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            <Snackbar open={snackbarOpen} autoHideDuration={6000} onClose={handleCloseSnackbar}>
                <Alert onClose={handleCloseSnackbar} severity={snackbarSeverity} sx={{ width: '100%' }}>
                    {snackbarMessage}
                </Alert>
            </Snackbar>
        </div>
    );
}

export default Status;
