import React from "react";
import "./CreateAdvogado.css";
import Card from "../../components/Card";
import FormGroup from "../../components/FormGroup";
import axios from "axios";
import Button from '@mui/material/Button';
import ButtonGroup from '@mui/material/ButtonGroup';

export default class CreateAdvogado extends React.Component{

    state = {
        login: ' ',
        senha:' ',
        nome:' ',
        cpf:' ',
        inscricao:' ',
        estadoDeEmissao:' ',
        filiacao:' ',
        dataDeNascimento:' '
    }

    cadastrar = async() => {

        await axios.post('http://localhost:8080/api/advogado',
        {
         login: this.state.nome,
         senha: this.state.cartel
        }
        ).then(response => 
         {
           console.log(response);
         }
         ).catch(error => 
           {
             console.log(error.response);
           });
           console.log('request finished');
       }

       cancel = () =>{
        console.log('cancel');
      }

      render(){
        return(

        <div className="container">
                <div className="row">
                    <div className="col-lg-12">
                        <div className="bs-component">
                        <div style={{marginTop: '70px'}}>
                        <ButtonGroup variant="contained" aria-label="Basic button group" >
                            <Button>One</Button>
                            <Button>Two</Button>
                            <Button>Three</Button>
                            </ButtonGroup>
                        <div style={{width: 1100, height: 430, background: 'white', border: '2px #838383 solid'}}></div>




                            <button onClick={this.cadastrar} type="button" className="btn btn-success">
                                <i className="pi pi-save"></i> Cadastrar
                            </button>
                            <button onClick={this.cancel} type="button" className="btn btn-danger">
                                <i className="pi pi-cancel"></i> Cancelar
                            </button>
                            </div>
                        </div>
                    </div>
                </div>


        </div>
        )
      }

}

