import React, { useState, useEffect } from "react";
import "./Casos.css";
import TextField from '@mui/material/TextField';
import axios from "axios";
import Snackbar from '@mui/material/Snackbar';
import Alert from '@mui/material/Alert';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import Typography from '@mui/material/Typography';
import Grid from '@mui/material/Grid';

const Casos = () => {
    const [processos, setProcessos] = useState([]);
    const [clientes, setClientes] = useState({});
    const [snackbarOpen, setSnackbarOpen] = useState(false);
    const [snackbarMessage, setSnackbarMessage] = useState('');
    const [snackbarSeverity, setSnackbarSeverity] = useState('success');
    const [searchQuery, setSearchQuery] = useState('');
    const [filterQuery, setFilterQuery] = useState('');

    useEffect(() => {
        const fetchProcessos = async () => {
            try {
                const response = await axios.get('http://localhost:8080/api/processo');
                const fetchedProcessos = response.data;
                setProcessos(fetchedProcessos);
    
                console.log("Processos recebidos:", fetchedProcessos);
    
                if (fetchedProcessos.length > 0) {
                    fetchedProcessos.forEach((processo, index) => {
                        console.log(`Processo ${index + 1}:`, processo);
                    });
                }
    
                // Extrair os IDs dos clientes
                const clienteIds = [...new Set(fetchedProcessos.map(processo => processo.clienteId?.id))].filter(id => id !== undefined);
                console.log("IDs dos clientes:", clienteIds);
    
                // Buscar detalhes dos clientes
                const clientePromises = clienteIds.map(id => 
                    axios.get(`http://localhost:8080/api/cliente/${id}`)
                );
    
                const clienteResponses = await Promise.all(clientePromises);
    
                const clienteMap = {};
                clienteResponses.forEach(res => {
                    if (res && res.data) {
                        console.log("Cliente recebido:", res.data);
                        clienteMap[res.data.id] = res.data.nome;
                    }
                });
                setClientes(clienteMap);
    
            } catch (error) {
                console.error("Erro ao buscar processos ou clientes:", error);
                setSnackbarMessage('Erro ao buscar processos ou clientes!');
                setSnackbarSeverity('error');
                setSnackbarOpen(true);
            }
        };
        fetchProcessos();
    }, []);
    

    const handleSearchChange = (event) => {
        setSearchQuery(event.target.value);
    };

    const handleFilterChange = (event) => {
        setFilterQuery(event.target.value);
    };

    const handleCloseSnackbar = () => {
        setSnackbarOpen(false);
    };

    const filteredProcessos = processos.filter(processo => 
        (processo.titulo && processo.titulo.toLowerCase().includes(searchQuery.toLowerCase())) &&
        (filterQuery === '' || processo.status === filterQuery)
    );

    return (
        <div className="container mt-5">
            <div style={{ marginTop: '70px' }}>
                <div className="row">
                    {/* Lado Esquerdo - Pesquisa e Filtro */}
                    <div className="col-md-4 mb-4">
                        <TextField 
                            label="Pesquisar Processos" 
                            variant="outlined" 
                            fullWidth 
                            onChange={handleSearchChange} 
                            className="mb-3"
                        />
                        <TextField 
                            label="Filtrar por Status" 
                            variant="outlined" 
                            fullWidth 
                            onChange={handleFilterChange} 
                        />
                    </div>

                    {/* Lado Direito - Card de Processos */}
                    <div className="col-md-8">
                        {filteredProcessos.length > 0 ? (
                            filteredProcessos.map(processo => (
                                <Card 
                                    className="mb-4" 
                                    key={processo.id} 
                                    sx={{ backgroundColor: '#f0f0f0' }} // Cinza claro
                                >
                                    <CardContent>
                                        <Grid container spacing={2}>
                                            {/* Coluna da esquerda - Cliente */}
                                            <Grid item xs={4}>
                                                <Typography 
                                                    variant="body1" 
                                                    component="div" 
                                                    sx={{ color: '#4a4a4a' }} // Cinza escuro
                                                >
                                                    Cliente:
                                                </Typography>
                                                <Typography 
                                                    variant="body2" 
                                                    color="text.secondary" 
                                                    sx={{ fontWeight: 'bold', color: '#4a4a4a' }} // Negrito e cinza escuro
                                                >
                                                    {clientes[processo.clienteId?.id] || "Cliente n√£o encontrado"}
                                                </Typography>
                                            </Grid>

                                            {/* Coluna do meio - T√≠tulo */}
                                            <Grid item xs={4}>
                                                <Typography 
                                                    variant="h5" 
                                                    component="div" 
                                                    sx={{ fontSize: '1.33rem', fontWeight: 'bold', color: '#4a4a4a' }} // Reduzindo o tamanho da fonte, negrito e cinza escuro
                                                >
                                                    {processo.titulo}
                                                </Typography>
                                                <Typography 
                                                    variant="body2" 
                                                    color="text.secondary" 
                                                    sx={{ color: '#4a4a4a' }} // Cinza escuro
                                                >
                                                    {processo.descricao}
                                                </Typography>
                                            </Grid>

                                            {/* Coluna da direita - Data */}
                                            <Grid item xs={4} textAlign="right">
                                                <Typography 
                                                    variant="body2" 
                                                    color="text.secondary" 
                                                    sx={{ color: '#4a4a4a' }} // Cinza escuro
                                                >
                                                    Prazo:
                                                </Typography>
                                                <Typography 
                                                    variant="body2" 
                                                    color="text.secondary" 
                                                    sx={{ fontWeight: 'bold', color: '#4a4a4a' }} // Negrito e cinza escuro
                                                >
                                                    {new Date(processo.prazo).toLocaleDateString()}
                                                </Typography>
                                            </Grid>
                                        </Grid>
                                    </CardContent>
                                </Card>
                            ))
                        ) : (
                            <Typography variant="body1">Nenhum processo encontrado.</Typography>
                        )}
                    </div>
                </div>
                <Snackbar 
                    open={snackbarOpen} 
                    autoHideDuration={6000} 
                    onClose={handleCloseSnackbar}
                >
                    <Alert 
                        onClose={handleCloseSnackbar} 
                        severity={snackbarSeverity} 
                        sx={{ width: '100%' }}
                    >
                        {snackbarMessage}
                    </Alert>
                </Snackbar>
            </div>
        </div>
    );
}

export default Casos;
