import React, { useState, useEffect } from "react";
import "./Casos.css";
import TextField from '@mui/material/TextField';
import axios from "axios";
import Snackbar from '@mui/material/Snackbar';
import Alert from '@mui/material/Alert';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import Typography from '@mui/material/Typography';
import Grid from '@mui/material/Grid';
import Divider from '@mui/material/Divider';

const Casos = () => {
    const [processos, setProcessos] = useState([]);
    const [clientes, setClientes] = useState({});
    const [snackbarOpen, setSnackbarOpen] = useState(false);
    const [snackbarMessage, setSnackbarMessage] = useState('');
    const [snackbarSeverity, setSnackbarSeverity] = useState('success');
    const [searchQuery, setSearchQuery] = useState('');
    const [filterQuery, setFilterQuery] = useState('');

    useEffect(() => {
        const fetchProcessos = async () => {
            try {
                const response = await axios.get('http://localhost:8080/api/processo');
                const fetchedProcessos = response.data;
                setProcessos(fetchedProcessos);
    
                // Fetch clientes based on fetchedProcessos
                const clienteIds = [...new Set(fetchedProcessos.map(processo => processo.clienteId).filter(id => id !== undefined && id !== null))];
                const clientePromises = clienteIds.map(id => axios.get(`http://localhost:8080/api/cliente/${id}`));
                const clienteResponses = await Promise.all(clientePromises);
                const clienteMap = {};
                clienteResponses.forEach(res => {
                    if (res && res.data) {
                        clienteMap[res.data.id] = res.data.nome;
                    }
                });
                setClientes(clienteMap);
    
            } catch (error) {
                console.error("Erro ao buscar processos ou clientes:", error);
                setSnackbarMessage('Erro ao buscar processos ou clientes!');
                setSnackbarSeverity('error');
                setSnackbarOpen(true);
            }
        };
        fetchProcessos();
    }, []);

    const handleSearchChange = (event) => {
        setSearchQuery(event.target.value);
    };

    const handleFilterChange = (event) => {
        setFilterQuery(event.target.value);
    };

    const handleCloseSnackbar = () => {
        setSnackbarOpen(false);
    };

    const filteredProcessos = processos.filter(processo => 
        (processo.titulo && processo.titulo.toLowerCase().includes(searchQuery.toLowerCase())) &&
        (filterQuery === '' || processo.status === filterQuery)
    );

    return (
        <div className="container mt-5">
            <div style={{ marginTop: '70px' }}>
                <div className="row">
                    {/* Lado Esquerdo - Pesquisa e Filtro */}
                    <div className="col-md-3 mb-4">
                        <Typography variant="h6" sx={{ color: '#6a6a6a' }}>Título</Typography>
                        <TextField 
                            placeholder="Pesquisar Processos" 
                            variant="outlined" 
                            fullWidth 
                            onChange={handleSearchChange} 
                            className="mb-3"
                            sx={{
                                input: { color: '#6a6a6a' }, // Cor do texto dentro do campo
                                '& .MuiOutlinedInput-root': {
                                    '& fieldset': { borderColor: '#6a6a6a' }, // Cor da borda
                                    '&:hover fieldset': { borderColor: '#6a6a6a' }, // Cor da borda ao passar o mouse
                                    '&.Mui-focused fieldset': { borderColor: '#6a6a6a' } // Cor da borda ao focar
                                }
                            }}
                        />
                        <Typography variant="h6" sx={{ color: '#6a6a6a' }}>Status</Typography>
                        <TextField 
                            placeholder="Filtrar por Status" 
                            variant="outlined" 
                            fullWidth 
                            onChange={handleFilterChange} 
                            sx={{
                                input: { color: '#6a6a6a' }, // Cor do texto dentro do campo
                                '& .MuiOutlinedInput-root': {
                                    '& fieldset': { borderColor: '#6a6a6a' }, // Cor da borda
                                    '&:hover fieldset': { borderColor: '#6a6a6a' }, // Cor da borda ao passar o mouse
                                    '&.Mui-focused fieldset': { borderColor: '#6a6a6a' } // Cor da borda ao focar
                                }
                            }}
                        />
                    </div>

                    {/* Barra vertical separadora */}
                    <div className="col-md-1 d-flex align-items-center justify-content-center">
                        <div style={{ width: '4px', height: '100%', backgroundColor: '#f0f0f0' }}></div>
                    </div>

                    {/* Lado Direito - Card de Processos */}
                    <div className="col-md-8">
                        {filteredProcessos.length > 0 ? (
                            filteredProcessos.map(processo => (
                                <Card 
                                    className="mb-4" 
                                    key={processo.id} 
                                    sx={{ backgroundColor: '#f0f0f0' }}
                                >
                                    <CardContent>
                                        <Grid container spacing={2}>
                                            {/* Coluna da esquerda - Cliente */}
                                            <Grid item xs={4}>
                                                <Typography variant="body1" sx={{ color: '#4a4a4a' }}>
                                                    Cliente:
                                                </Typography>
                                                <Typography variant="body2" sx={{ fontWeight: 'bold', color: '#4a4a4a' }}>
                                                    {clientes[processo.clienteId] || "Cliente não encontrado"}
                                                </Typography>
                                            </Grid>

                                            {/* Coluna do meio - Título */}
                                            <Grid item xs={4}>
                                                <Typography variant="h5" sx={{ fontSize: '18px', fontWeight: 'bold', color: '#4a4a4a' }}>
                                                    {processo.titulo}
                                                </Typography>
                                                <Typography variant="body2" sx={{ color: '#6a6a6a' }}>
                                                    {processo.descricao}
                                                </Typography>
                                            </Grid>

                                            {/* Coluna da direita - Data */}
                                            <Grid item xs={4} textAlign="right">
                                                <Typography variant="body2" sx={{color: '#6a6a6a' }}>
                                                    Prazo:
                                                </Typography>
                                                <Typography variant="body2" sx={{ fontWeight: 'bold', color: '#6a6a6a' }}>
                                                    {new Date(processo.prazo).toLocaleDateString()}
                                                </Typography>
                                            </Grid>
                                        </Grid>
                                    </CardContent>
                                </Card>
                            ))
                        ) : (
                            <Typography variant="body1">Nenhum processo encontrado.</Typography>
                        )}
                    </div>
                </div>
                <Snackbar 
                    open={snackbarOpen} 
                    autoHideDuration={6000} 
                    onClose={handleCloseSnackbar}
                >
                    <Alert 
                        onClose={handleCloseSnackbar} 
                        severity={snackbarSeverity} 
                        sx={{ width: '100%' }}
                    >
                        {snackbarMessage}
                    </Alert>
                </Snackbar>
            </div>
        </div>
    );
}

export default Casos;
