import React, { useState, useRef, useEffect } from "react";
import "./Anexos.css";
import { TextField, Button, ButtonGroup, Snackbar, Alert, Chip, Box, Select, MenuItem, FormControl, InputLabel } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../../../contexts/AuthContext';

const Anexos = () => {
    const navigate = useNavigate();
    const { user } = useAuth();

    const [state, setState] = useState({
        titulo: '',
        prazo: '',
        nome: '',
        projetoId: '',
    });

    const [projetos, setProjetos] = useState([]); // Estado para armazenar a lista de projetos
    const [tags, setTags] = useState([]);
    const tagInputRef = useRef(null);

    const [files, setFiles] = useState([]);
    const [fileHistory, setFileHistory] = useState([]);

    const [snackbarOpen, setSnackbarOpen] = useState(false);
    const [snackbarMessage, setSnackbarMessage] = useState('');
    const [snackbarSeverity, setSnackbarSeverity] = useState('success');

    useEffect(() => {
        if (user) {
            setState((prevState) => ({
                ...prevState,
                nome: user,
            }));
        }

        // Carrega a lista de projetos do backend
        const fetchProjetos = async () => {
            try {
                const response = await axios.get("http://localhost:8080/api/processo");
                setProjetos(response.data);
            } catch (error) {
                console.error("Erro ao buscar projetos:", error);
            }
        };

        fetchProjetos();
    }, [user]);

    const handleChange = (campo) => (event) => {
        setState({ ...state, [campo]: event.target.value });
    };

    const handleTagChange = (event, newValue) => {
        setTags(newValue);
        if (tagInputRef.current) {
            setTimeout(() => {
                tagInputRef.current.focus();
            }, 100);
        }
    };

    const handleDeleteTag = (tagToDelete) => () => {
        setTags((tags) => tags.filter((tag) => tag !== tagToDelete));
    };

    const handleFileChange = (event) => {
        const newFiles = Array.from(event.target.files).filter((file) =>
            ["video/mp4", "application/pdf", "audio/mpeg", "image/png"].includes(file.type)
        );
        setFiles([...files, ...newFiles]);
    };

    const handleDeleteFile = (fileToDelete) => () => {
        setFiles(files.filter((file) => file !== fileToDelete));
    };

    const salvar = async () => {
        if (!state.projetoId) {
            setSnackbarMessage("Nenhum projeto selecionado.");
            setSnackbarSeverity("error");
            setSnackbarOpen(true);
            return;
        }
    
        const formData = new FormData();
    
        // Adiciona os arquivos ao FormData
        files.forEach((file) => {
            formData.append('files', file);
        });
    
        // Cria o objeto processoData
        const processoData = {
            titulo: state.titulo,
            descricao: state.descricao,
            prazo: state.prazo,
            registroReceita: state.registroReceita,
            tag: tags,
            status: state.status,
            usuario: state.nome
        };
    
        // Adiciona o JSON como uma string ao FormData
        formData.append('processoDTO', JSON.stringify(processoData));
    
        try {
            const response = await axios.put(`http://localhost:8080/api/processo/${state.projetoId}`, formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });
    
            if (response.status >= 200 && response.status < 300) {
                setSnackbarMessage("Arquivos salvos com sucesso!");
                setSnackbarSeverity("success");
                setSnackbarOpen(true);
            } else {
                setSnackbarMessage("Erro ao salvar os arquivos.");
                setSnackbarSeverity("error");
                setSnackbarOpen(true);
            }
        } catch (error) {
            console.error("Erro na requisição:", error);
            setSnackbarMessage("Erro ao salvar os arquivos.");
            setSnackbarSeverity("error");
            setSnackbarOpen(true);
        }
    };
    
    
    
    
    
    

    const cancel = () => {
        console.log('cancel');
    };

    const goToProjetos = () => {
        navigate("/sessao/projetos");
    };

    const goToStatus = () => {
        navigate("/sessao/status");
    };

    const handleCloseSnackbar = () => {
        setSnackbarOpen(false);
    };

    return (
        <div className="container">
            <div className="row">
                <div className="col-lg-12">
                    <div className="bs-component">
                        <div style={{ marginTop: '80px' }}>
                            <ButtonGroup style={{ height: '30px' }} variant="contained" aria-label="Basic button group">
                                <Button style={{ backgroundColor: 'white', color: 'grey', fontSize: 10, width: 120 }} onClick={goToProjetos}>Processos</Button>
                                <Button style={{ backgroundColor: 'white', color: 'grey', fontSize: 10, width: 120 }} onClick={goToStatus}>Status</Button>
                                <Button style={{ backgroundColor: 'grey', fontSize: 10, alignItems: 'center', width: 120 }}>Anexos</Button>
                            </ButtonGroup>
                            <div style={{ width: 1100, height: 470, background: 'white', border: '2px #838383 solid' }}>

                                <div style={{ marginTop: '10px', marginLeft: '30px' }}>
                                    <FormControl fullWidth>
                                        <InputLabel id="select-projeto-label">Selecione o Projeto</InputLabel>
                                        <Select
                                            labelId="select-projeto-label"
                                            id="select-projeto"
                                            value={state.projetoId}
                                            label="Selecione o Projeto"
                                            onChange={handleChange('projetoId')}
                                        >
                                            {projetos.map((projeto) => (
                                                <MenuItem key={projeto.id} value={projeto.id}>
                                                    {projeto.titulo}
                                                </MenuItem>
                                            ))}
                                        </Select>
                                    </FormControl>
                                </div>

                                <div style={{ marginTop: '20px', marginLeft: '650px' }}>
                                    <div style={{ width: 200, height: 40, color: '#838383', fontSize: 12, fontFamily: 'Inter', fontWeight: '400', wordWrap: 'break-word' }}>ANEXAR ARQUIVOS</div>
                                    <input
                                        type="file"
                                        multiple
                                        accept=".mp4,.pdf,.mp3,.png"
                                        onChange={handleFileChange}
                                    />
                                    <Box sx={{ display: 'flex', flexDirection: 'column', gap: 1, marginTop: '8px' }}>
                                        {files.map((file, index) => (
                                            <Chip
                                                key={index}
                                                label={file.name}
                                                onDelete={handleDeleteFile(file)}
                                            />
                                        ))}
                                    </Box>
                                </div>

                                <div style={{ marginTop: '20px', marginLeft: '30px' }}>
                                    <Box sx={{ maxHeight: 350, overflow: 'auto' }}>
                                        <h4>Histórico de Alterações</h4>
                                        {fileHistory.map((entry, index) => (
                                            <div key={index}>
                                                <p><strong>Data:</strong> {entry.date}</p>
                                                <p><strong>Usuário:</strong> {entry.user}</p>
                                                <p><strong>Arquivos:</strong> {entry.files.join(', ')}</p>
                                                <hr />
                                            </div>
                                        ))}
                                    </Box>
                                </div>
                            </div>
                            <div style={{ marginTop: '20px' }}>
                                <Button onClick={cancel} variant="contained" style={{ width: 120, height: 40, backgroundColor: 'grey' }}>Cancelar</Button>
                                <Button onClick={salvar} variant="contained" style={{ width: 120, height: 40, backgroundColor: 'grey', marginLeft: '850px' }}>Salvar</Button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            <Snackbar open={snackbarOpen} autoHideDuration={6000} onClose={handleCloseSnackbar}>
                <Alert onClose={handleCloseSnackbar} severity={snackbarSeverity} sx={{ width: '100%' }}>
                    {snackbarMessage}
                </Alert>
            </Snackbar>
        </div>
    );
};

export default Anexos;
