import React, { useState, useRef, useEffect } from "react";
import "./Anexos.css";
import { TextField, Button, ButtonGroup, Snackbar, Alert, Chip, Box } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../../contexts/AuthContext'; // Importar useAuth

const Anexos = () => {
    const navigate = useNavigate();
    const { user } = useAuth(); // Usar useAuth para obter o usuário

    const [state, setState] = useState({
        titulo: '',
        prazo: '',
        nome: '',
    });

    const [tags, setTags] = useState([]);
    const tagInputRef = useRef(null);

    const [files, setFiles] = useState([]);
    const [fileHistory, setFileHistory] = useState([]);

    const [snackbarOpen, setSnackbarOpen] = useState(false);
    const [snackbarMessage, setSnackbarMessage] = useState('');
    const [snackbarSeverity, setSnackbarSeverity] = useState('success');

    useEffect(() => {
        if (user) {
            console.log("USER "+ user.nome)
            setState((prevState) => ({
                ...prevState,
                nome: user.nome,
            }));
        }
    }, [user]);

    const handleChange = (campo) => (event) => {
        setState({ ...state, [campo]: event.target.value });
    };

    const handleTagChange = (event, newValue) => {
        setTags(newValue);
        if (tagInputRef.current) {
            setTimeout(() => {
                tagInputRef.current.focus();
            }, 100);
        }
    };

    const handleDeleteTag = (tagToDelete) => () => {
        setTags((tags) => tags.filter((tag) => tag !== tagToDelete));
    };

    const handleFileChange = (event) => {
        const newFiles = Array.from(event.target.files).filter((file) =>
            ["video/mp4", "application/pdf", "audio/mpeg", "image/png"].includes(file.type)
        );
        setFiles([...files, ...newFiles]);
    };

    const handleDeleteFile = (fileToDelete) => () => {
        setFiles(files.filter((file) => file !== fileToDelete));
    };

    const salvar = async () => {
        const currentDateTime = new Date().toLocaleString();
        const newHistoryEntry = {
            date: currentDateTime,
            user: user ? user.nome : 'Usuário Desconhecido',
            files: files.map(file => file.name),
        };
        setFileHistory([...fileHistory, newHistoryEntry]);
        setFiles([]);  // Limpa os arquivos após o salvamento
        setSnackbarMessage("Arquivos salvos com sucesso!");
        setSnackbarSeverity("success");
        setSnackbarOpen(true);
    };

    const cancel = () => {
        console.log('cancel');
    };

    const goToProjetos = () => {
        navigate("/sessao/projetos");
    };

    const goToStatus = () => {
        navigate("/sessao/status");
    };

    const handleCloseSnackbar = () => {
        setSnackbarOpen(false);
    };

    return (
        <div className="container">
            <div className="row">
                <div className="col-lg-12">
                    <div className="bs-component">
                        <div style={{marginTop: '80px'}}>
                            <ButtonGroup style={{height:'30px'}} variant="contained" aria-label="Basic button group">
                                <Button style={{ backgroundColor: 'white', color: 'grey' , fontSize: 10, width: 120 }} onClick={goToProjetos}>Processos</Button>
                                <Button style={{ backgroundColor: 'white', color: 'grey' , fontSize: 10, width: 120 }} onClick={goToStatus}>Status</Button>
                                <Button style={{ backgroundColor: 'grey', fontSize: 10, alignItems: 'center', width: 120  }}>Anexos</Button>
                            </ButtonGroup>
                            <div style={{width: 1100, height: 500, background: 'white', border: '2px #838383 solid'}}>
                                <div style={{marginTop: '25px', marginLeft: '30px'}}>
                                    <div style={{width: 80, height: 40, color: '#838383', fontSize: 12, fontFamily: 'Inter', fontWeight: '400', wordWrap: 'break-word'}}>TITULO</div>
                                    <TextField
                                        hiddenLabel
                                        id="filled-hidden-label-small"
                                        defaultValue=""
                                        variant="filled"
                                        size="small"
                                        sx={{
                                            marginTop: '-15px',
                                            width: '250px',
                                            '& .MuiInputBase-root': {
                                                height: '32px',
                                                padding: '0 14px',
                                                '& input': {
                                                    height: '20px',
                                                    padding: '0',
                                                    fontSize: '13px',
                                                },
                                            },
                                        }}
                                        value={state.titulo}
                                        onChange={handleChange('titulo')} 
                                    />
                                </div>
                                <div style={{marginTop: '-60px', marginLeft: '650px'}}>
                                    <div style={{width: 200, height: 40, color: '#838383', fontSize: 12, fontFamily: 'Inter', fontWeight: '400', wordWrap: 'break-word'}}>ANEXAR ARQUIVOS</div>
                                    <input
                                        type="file"
                                        multiple
                                        accept=".mp4,.pdf,.mp3,.png"
                                        onChange={handleFileChange}
                                    />
                                    <Box sx={{ display: 'flex', flexDirection: 'column', gap: 1, marginTop: '8px' }}>
                                        {files.map((file, index) => (
                                            <Chip
                                                key={index}
                                                label={file.name}
                                                onDelete={handleDeleteFile(file)}
                                            />
                                        ))}
                                    </Box>
                                </div>
                                <div style={{marginTop: '20px', marginLeft: '30px'}}>
                                    <Box sx={{ maxHeight: 150, overflow: 'auto', border: '1px solid #ccc', padding: 2, borderRadius: 2 }}>
                                        <h4>Histórico de Alterações</h4>
                                        {fileHistory.map((entry, index) => (
                                            <div key={index}>
                                                <p><strong>Data:</strong> {entry.date}</p>
                                                <p><strong>Usuário:</strong> {entry.user}</p>
                                                <p><strong>Arquivos:</strong> {entry.files.join(', ')}</p>
                                                <hr />
                                            </div>
                                        ))}
                                    </Box>
                                </div>
                            </div>
                            <div style={{ marginTop: '20px' }}>
                                <Button onClick={salvar} variant="contained" style={{width: 120, height: 40, backgroundColor: 'grey'}}>Salvar</Button>
                                <Button onClick={cancel} variant="contained" style={{width: 120, height: 40, backgroundColor: 'grey', marginLeft: '850px'}}>Cancelar</Button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            <Snackbar open={snackbarOpen} autoHideDuration={6000} onClose={handleCloseSnackbar}>
                <Alert onClose={handleCloseSnackbar} severity={snackbarSeverity} sx={{ width: '100%' }}>
                    {snackbarMessage}
                </Alert>
            </Snackbar>
        </div>
    );
};

export default Anexos;
