import React, { useEffect, useState, useRef } from "react";
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import { Box, Typography } from "@mui/material";
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import { DateCalendar } from '@mui/x-date-pickers/DateCalendar';
import dayjs from 'dayjs';

const Home = () => {
    const [events, setEvents] = useState([]);
    const [selectedDate, setSelectedDate] = useState(dayjs());
    const calendarRef = useRef(null);
    const [isUpdating, setIsUpdating] = useState(false);

    useEffect(() => {
        // Buscar processos do backend
        fetch('http://localhost:8080/api/processo')
            .then(response => response.json())
            .then(data => {
                const eventsData = data.map(processo => ({
                    title: processo.titulo,
                    date: processo.prazo,
                }));
                setEvents(eventsData);
            })
            .catch(error => console.error('Erro ao buscar processos:', error));
    }, []);

    const handleDateChange = (newDate) => {
        // Só atualizar se o mês não tiver mudado
        if (!isUpdating && !selectedDate.isSame(newDate, 'month')) {
            setIsUpdating(true);
            setSelectedDate(newDate);

            // Atualiza o FullCalendar para a nova data selecionada
            if (calendarRef.current) {
                const calendarApi = calendarRef.current.getApi();
                calendarApi.gotoDate(newDate.toDate());
            }
            setIsUpdating(false);
        }
    };

    const handleDatesSet = (dateInfo) => {
        // Atualizar a data selecionada quando o mês do FullCalendar mudar
        const newDate = dayjs(dateInfo.start);
        if (!selectedDate.isSame(newDate, 'month')) {
            setSelectedDate(newDate);
        }
    };

    return (
        <LocalizationProvider dateAdapter={AdapterDayjs}>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', height: '100vh', padding: 2 }}>
                {/* FullCalendar à esquerda ocupando mais da metade da tela */}
                <Box sx={{ width: '65%', backgroundColor: '#f0f0f0', padding: 2 }}>
                    <Typography variant="h6" gutterBottom>
                        Calendário Grande
                    </Typography>
                    <FullCalendar
                        ref={calendarRef}
                        plugins={[dayGridPlugin]}
                        initialView="dayGridMonth"
                        events={events}
                        headerToolbar={{
                            left: '',
                            center: 'title',
                            right: '',
                        }}
                        datesSet={handleDatesSet}
                        initialDate={selectedDate.toDate()}
                    />
                </Box>

                {/* Calendário pequeno na direita */}
                <Box sx={{ width: '35%', backgroundColor: '#ffffff', padding: 2 }}>
                    <Typography variant="h6" gutterBottom>
                        Selecionar Data
                    </Typography>
                    <DateCalendar
                        value={selectedDate}
                        onChange={handleDateChange}
                        views={['year', 'month', 'day']}
                        openTo="month"
                    />
                </Box>
            </Box>
        </LocalizationProvider>
    );
};

export default Home;
