import React, { useEffect, useState } from "react";
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import { Box, Typography, IconButton } from "@mui/material";
import { DateCalendar } from '@mui/x-date-pickers/DateCalendar';
import { DemoItem } from '@mui/x-date-pickers/internals/demo';
import dayjs from 'dayjs';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import ArrowForwardIcon from '@mui/icons-material/ArrowForward';

const Home = () => {
    const [events, setEvents] = useState([]);
    const [currentDate, setCurrentDate] = useState(dayjs());

    useEffect(() => {
        // Buscar processos do backend
        fetch('http://localhost:8080/api/processo')
            .then(response => response.json())
            .then(data => {
                // Converter dados para o formato de eventos do FullCalendar
                const eventsData = data.map(processo => ({
                    title: processo.titulo,
                    date: processo.prazo,
                }));
                setEvents(eventsData);
            })
            .catch(error => console.error('Erro ao buscar processos:', error));
    }, []);

    const handleDateChange = (date) => {
        setCurrentDate(date);
    };

    const handlePrevMonth = () => {
        setCurrentDate(currentDate.subtract(1, 'month'));
    };

    const handleNextMonth = () => {
        setCurrentDate(currentDate.add(1, 'month'));
    };

    return (
        <LocalizationProvider dateAdapter={AdapterDayjs}>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', height: '100vh', padding: 2 }}>
                {/* FullCalendar à esquerda ocupando metade da tela */}
                <Box sx={{ width: '65%', backgroundColor: '#f0f0f0', padding: 2 }}>
                    <Typography variant="h6" gutterBottom>
                        Calendário Grande
                    </Typography>
                    <FullCalendar
                        plugins={[dayGridPlugin]}
                        initialView="dayGridMonth"
                        events={events}
                        date={currentDate.toDate()} // Sincronizar com a data atual
                        initialDate={currentDate.toDate()}
                    />
                </Box>

                {/* Calendário pequeno à direita */}
                <Box sx={{ width: '35%', backgroundColor: '#ffffff', padding: 2 }}>
                    <Typography variant="h6" gutterBottom>
                        Calendário Pequeno
                    </Typography>
                    <Box sx={{ display: 'flex', alignItems: 'center', marginBottom: 2 }}>
                        <IconButton onClick={handlePrevMonth}>
                            <ArrowBackIcon />
                        </IconButton>
                        <Typography variant="h6">
                            {currentDate.format('MMMM YYYY')}
                        </Typography>
                        <IconButton onClick={handleNextMonth}>
                            <ArrowForwardIcon />
                        </IconButton>
                    </Box>
                    <DemoItem label={'"day"'}>
                        <DateCalendar 
                            views={['day']}
                            value={currentDate} 
                            onChange={handleDateChange} // Sincroniza quando a data muda
                        />
                    </DemoItem>
                </Box>
            </Box>
        </LocalizationProvider>
    );
};

export default Home;
