import React from "react";
import { Box, Typography } from "@mui/material";
import { DateCalendar } from '@mui/x-date-pickers/DateCalendar';
import { DemoItem } from '@mui/x-date-pickers/internals/demo';
import dayjs from 'dayjs';

const Home = () => {
    return (
        <Box sx={{ display: 'flex', justifyContent: 'space-between', height: '100vh', padding: 2 }}>
            {/* Calendário grande à esquerda ocupando metade da tela */}
            <Box sx={{ width: '50%', backgroundColor: '#f0f0f0', padding: 2 }}>
                <Typography variant="h6" gutterBottom>
                    Calendário Grande
                </Typography>
                {/* Componente de calendário principal */}
                {/* Substitua isso pelo calendário que você está usando na esquerda */}
            </Box>

            {/* Calendários pequenos na direita */}
            <Box sx={{ width: '20%', backgroundColor: '#ffffff', padding: 2 }}>
                <Typography variant="h6" gutterBottom>
                    Calendários
                </Typography>
                <DemoItem label="disabled">
                    <DateCalendar defaultValue={dayjs('2022-04-17')} disabled />
                </DemoItem>
                <DemoItem label="readOnly">
                    <DateCalendar defaultValue={dayjs('2022-04-17')} readOnly />
                </DemoItem>
            </Box>
        </Box>
    );
};

export default Home;
