import React, { useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';  // Certifique-se de que o caminho est치 correto
import { DatePicker, LocalizationProvider } from '@mui/lab';
import AdapterDateFns from '@mui/lab/AdapterDateFns';
import { Box, TextField } from '@mui/material';


function Calendarios() {
    const [date, setDate] = useState(new Date());
    const [smallDate, setSmallDate] = useState(new Date());

    return (
        <Box sx={{ display: 'flex', justifyContent: 'space-between', padding: 2 }}>
            {/* Calend치rio grande na esquerda ocupando metade da tela */}
            <Box sx={{ width: '50%' }}>
                <Calendar
                    onChange={setDate}
                    value={date}
                    className="custom-calendar"
                />
            </Box>

            {/* Calend치rio pequeno na direita */}
            <Box sx={{ width: '25%', display: 'flex', justifyContent: 'flex-end' }}>
                <LocalizationProvider dateAdapter={AdapterDateFns}>
                    <DatePicker
                        label="Small Calendar"
                        value={smallDate}
                        onChange={(newValue) => setSmallDate(newValue)}
                        renderInput={(params) => <TextField {...params} variant="outlined" size="small" />}
                    />
                </LocalizationProvider>
            </Box>
        </Box>
    );
}

export default Calendarios;
