// Casos.js
import React, { useState, useEffect } from "react";
import "./Casos.css";
import TextField from '@mui/material/TextField';
import axios from "axios";
import MenuItem from '@mui/material/MenuItem';
import Snackbar from '@mui/material/Snackbar';
import Alert from '@mui/material/Alert';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import Typography from '@mui/material/Typography';
import Grid from '@mui/material/Grid';

const Casos = () => {
  const [processos, setProcessos] = useState([]);
  const [clientes, setClientes] = useState({});
  const [searchQuery, setSearchQuery] = useState('');
  const [filterQuery, setFilterQuery] = useState('');
  const [clienteFilter, setClienteFilter] = useState('');

  useEffect(() => {
    const fetchProcessos = async () => {
      try {
        const response = await axios.get('http://localhost:8080/api/processo');
        setProcessos(response.data);

        const clienteIds = [...new Set(response.data.map(processo => processo.clienteId))];
        const clienteResponses = await Promise.all(
          clienteIds.map(id => axios.get(`http://localhost:8080/api/cliente/${id}`))
        );

        const clienteMap = clienteResponses.reduce((acc, res) => {
          acc[res.data.id] = res.data.nome;
          return acc;
        }, {});
        setClientes(clienteMap);
      } catch (error) {
        console.error("Erro ao buscar processos ou clientes:", error);
      }
    };
    fetchProcessos();
  }, []);

  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
  };

  const handleFilterChange = (event) => {
    setFilterQuery(event.target.value);
  };

  const handleClienteChange = (event) => {
    setClienteFilter(event.target.value);
  };

  const filteredProcessos = processos.filter(processo => {
    const matchesTitulo = processo.titulo?.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesStatus = !filterQuery || processo.status === filterQuery;
    const matchesCliente = !clienteFilter || processo.clienteId === clienteFilter;

    return matchesTitulo && matchesStatus && matchesCliente;
  });

  return (
    <div className="container mt-5 casos-page">
      <div style={{ marginTop: '70px' }}>
        <div className="row">
          {/* Lado Esquerdo - Pesquisa e Filtros */}
          <div className="col-md-3 mb-4">
            <div className="filter-label">T√≠tulo</div>
            <TextField
              hiddenLabel
              variant="filled"
              size="small"
              fullWidth
              onChange={handleSearchChange}
              sx={{ width: '90%', marginTop: '10px' }}
            />

            <div className="filter-label">Status</div>
            <TextField
              hiddenLabel
              variant="filled"
              size="small"
              fullWidth
              onChange={handleFilterChange}
              sx={{ width: '90%', marginTop: '10px' }}
            />

            <div className="filter-label">Cliente</div>
            <TextField
              select
              value={clienteFilter}
              onChange={handleClienteChange}
              variant="filled"
              size="small"
              fullWidth
              sx={{ width: '90%', marginTop: '10px' }}
            >
              <MenuItem value="">Todos os Clientes</MenuItem>
              {Object.entries(clientes).map(([id, nome]) => (
                <MenuItem key={id} value={id}>
                  {nome}
                </MenuItem>
              ))}
            </TextField>
          </div>

          {/* Lado Direito - Card de Processos */}
          <div className="col-md-8">
            {filteredProcessos.length > 0 ? (
              filteredProcessos.map(processo => (
                <Card key={processo.id} className="mb-4" sx={{ backgroundColor: '#f0f0f0' }}>
                  <CardContent>
                    <Grid container spacing={2}>
                      <Grid item xs={4}>
                        <Typography variant="body1" sx={{ color: '#4a4a4a' }}>
                          Cliente:
                        </Typography>
                        <Typography variant="body2" sx={{ fontWeight: 'bold', color: '#4a4a4a' }}>
                          {clientes[processo.clienteId] || "Cliente n√£o encontrado"}
                        </Typography>
                      </Grid>
                      <Grid item xs={4}>
                        <Typography variant="h5" sx={{ fontSize: '18px', fontWeight: 'bold', color: '#4a4a4a' }}>
                          {processo.titulo}
                        </Typography>
                        <Typography variant="body2" sx={{ color: '#6a6a6a' }}>
                          {processo.descricao}
                        </Typography>
                      </Grid>
                      <Grid item xs={4} textAlign="right">
                        <Typography variant="body2" sx={{ color: '#6a6a6a' }}>
                          Prazo:
                        </Typography>
                        <Typography variant="body2" sx={{ fontWeight: 'bold', color: '#6a6a6a' }}>
                          {new Date(processo.prazo).toLocaleDateString()}
                        </Typography>
                      </Grid>
                    </Grid>
                  </CardContent>
                </Card>
              ))
            ) : (
              <Typography variant="body1">Nenhum processo encontrado.</Typography>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Casos;
