import React, { useState, useEffect } from "react";
import "./Casos.css";
import TextField from '@mui/material/TextField';
import axios from "axios";
import Snackbar from '@mui/material/Snackbar';
import Alert from '@mui/material/Alert';
import Button from '@mui/material/Button';

const Casos = () => {
    const [processos, setProcessos] = useState([]);
    const [snackbarOpen, setSnackbarOpen] = useState(false);
    const [snackbarMessage, setSnackbarMessage] = useState('');
    const [snackbarSeverity, setSnackbarSeverity] = useState('success');
    const [searchQuery, setSearchQuery] = useState('');
    const [filterQuery, setFilterQuery] = useState('');

    useEffect(() => {
        // Função para buscar processos do backend
        const fetchProcessos = async () => {
            try {
                const response = await axios.get('http://localhost:8080/api/processos');
                setProcessos(response.data);
            } catch (error) {
                setSnackbarMessage('Erro ao buscar processos!');
                setSnackbarSeverity('error');
                setSnackbarOpen(true);
            }
        };
        fetchProcessos();
    }, []);

    const handleSearchChange = (event) => {
        setSearchQuery(event.target.value);
    };

    const handleFilterChange = (event) => {
        setFilterQuery(event.target.value);
    };

    const handleCloseSnackbar = () => {
        setSnackbarOpen(false);
    };

    // Filtrando os processos com base na pesquisa e filtro
    const filteredProcessos = processos.filter(processo => 
        processo.titulo.toLowerCase().includes(searchQuery.toLowerCase()) &&
        (filterQuery === '' || processo.status === filterQuery)
    );

    return (
        <div className="container mt-4">
            <div className="row">
                {/* Lado Esquerdo - Pesquisa e Filtro */}
                <div className="col-md-4">
                    <div className="mb-3">
                        <TextField 
                            label="Pesquisar Processos" 
                            variant="outlined" 
                            fullWidth 
                            onChange={handleSearchChange} 
                        />
                    </div>
                    <div className="mb-3">
                        <TextField 
                            label="Filtrar por Status" 
                            variant="outlined" 
                            fullWidth 
                            onChange={handleFilterChange} 
                        />
                    </div>
                </div>

                {/* Lado Direito - Card de Processos */}
                <div className="col-md-8">
                    {filteredProcessos.length > 0 ? (
                        filteredProcessos.map(processo => (
                            <div className="card mb-3" key={processo.id}>
                                <div className="card-body">
                                    <h5 className="card-title">{processo.titulo}</h5>
                                    <p className="card-text">{processo.descricao}</p>
                                    <p className="card-text"><small className="text-muted">Prazo: {new Date(processo.prazo).toLocaleDateString()}</small></p>
                                    <p className="card-text"><small className="text-muted">Status: {processo.status}</small></p>
                                    <Button variant="contained" color="primary">
                                        Ver Detalhes
                                    </Button>
                                </div>
                            </div>
                        ))
                    ) : (
                        <p>Nenhum processo encontrado.</p>
                    )}
                </div>
            </div>
            <Snackbar 
                open={snackbarOpen} 
                autoHideDuration={6000} 
                onClose={handleCloseSnackbar}
            >
                <Alert 
                    onClose={handleCloseSnackbar} 
                    severity={snackbarSeverity} 
                    sx={{ width: '100%' }}
                >
                    {snackbarMessage}
                </Alert>
            </Snackbar>
        </div>
    );
}

export default Casos;
